"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[7387],{8078:e=>{e.exports=JSON.parse('{"tag":{"label":"docker","permalink":"/docs/tags/docker","allTagsPath":"/docs/tags","count":10,"items":[{"id":"vector-db/ChromaDB","title":"Chroma","description":"Learn how to run Chroma DB with Docker Compose, persist your embeddings, and perform basic vector search queries.","permalink":"/docs/VectorDB/ChromaDB"},{"id":"Databases/Vector-Database/Faiss","title":"Faiss","description":"FAISS (Facebook AI Similarity Search) is a powerful library for efficient similarity search and clustering of dense vectors. In this tutorial, you\'ll learn how to containerize a minimal FAISS-powered REST API using Flask and Docker Compose.","permalink":"/docs/Databases/Vector-Database/faiss-docker-rest-api"},{"id":"Databases/Vector-Database/Use-Case/Health-Search-Recommendation","title":"HealthSearch Recommender","description":"Build a semantic search engine to match user symptoms with health articles or products using Weaviate, SentenceTransformers, a FastAPI backend, and a React frontend. Fully Dockerized and production-ready.","permalink":"/docs/projects/healthsearch-weaviate"},{"id":"vector-db/Marqo","title":"Marqo","description":"Learn how to run Marqo, an open-source multi-modal vector search engine, with Docker and perform text and image queries.","permalink":"/docs/VectorDB/Marqo"},{"id":"Databases/Vector-Database/Use-Case/Personalized-Recommendation-System","title":"Personalized Recommendation","description":"Build a full-stack personalized recommendation system using FAISS for offline ANN-based vector search, SentenceTransformers for item/user embeddings, LightFM for collaborative filtering, FastAPI backend, and a React UI. Fully Dockerized.","permalink":"/docs/projects/personalized-recommendation-faiss"},{"id":"Databases/Vector-Database/Use-Case/Transaction-Logs","title":"PostgreSQL Transaction Logs","description":"Build a hybrid search system using PostgreSQL for storing ACID-compliant transaction logs and pgvector for semantic similarity search. Backend in FastAPI and frontend in React, fully Dockerized and production-ready.","permalink":"/docs/projects/transaction-search-pgvector"},{"id":"vector-db/Qdrant","title":"Qdrant","description":"Learn how to deploy Qdrant with Docker Compose, persist your vector data, and execute basic vector search queries.","permalink":"/docs/VectorDB/Qdrant"},{"id":"Databases/Vector-Database/Use-Case/Resuturrent-recommendation","title":"Restaurant Recommendation App","description":"Build a Zomato-style restaurant recommendation system using Milvus vector database, SentenceTransformers, FastAPI backend, and a React frontend UI. Dockerized and production-ready.","permalink":"/docs/projects/restaurant-recommender"},{"id":"Databases/Vector-Database/Use-Case/Sematic-search","title":"Semantic Search","description":"Build a full-stack semantic document similarity app using ChromaDB as the vector store, SentenceTransformers for embeddings, FastAPI backend, and a React frontend UI. Dockerized and production-ready.","permalink":"/docs/projects/document-similarity-chromadb"},{"id":"vector-db/use-cases/SemanticSearch","title":"Semanticsearch","description":"Learn how to run Marqo, an open-source multi-modal vector search engine, with Docker and perform text and image queries.","permalink":"/docs/VectorDB/UseCases/ChromaDB"}],"unlisted":false}}')}}]);